import { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import { Chart as ChartJS, CategoryScale, LinearScale, Title, Tooltip, Legend, PointElement, LineElement } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, Title, Tooltip, Legend, PointElement, LineElement);

const options = {
  indexAxis: 'x',
  responsive: true,
  plugins: {
    legend: {
      position: 'bottom',
    },
    title: {
      display: true,
      text: 'Apti marks obtained',
      font: {
        size: 18,
        weight: 'bold',
      },
    },
  },
  scales: {
    x: {
      grid: {
        display: false,
      },
    },
    y: {
      grid: {
        display: false,
      },
      ticks: {
        beginAtZero: true,
        stepSize: 1,
      },
    },
  },
};

const dataStyle = {
  label: 'Marks obtained',
  data: [],
  borderColor: 'rgba(238, 125, 49, 1)',
  borderWidth: 2,
  fill:true,
  pointRadius: 4,
  pointBorderWidth: 2,
  pointBackgroundColor: 'rgba(238, 125, 49, 1)',
  pointBorderColor: 'rgba(238, 125, 49, 1)',
  backgroundColor: 'rgba(238, 125, 49, 1)', 
  backgroundImage: 'linear-gradient(to bottom, rgba(255, 165, 0, 0.4), rgba(255, 165, 0, 0))',
};


const Apti = () => {


  const [data1, setData1] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const url = 'http://localhost:3001/getSingleData';
        const response = await axios.get(url);
        const data = response.data;
        console.log("testshare", data);
        setData1(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className='Agraph' style={{ width: '100%', maxWidth: '500px', height: '400px', margin: '60px auto' }}>
      <Line data={data} options={options} />
    </div>
  );
};

export default Apti;
