import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';
import { Chart as ChartJS, CategoryScale, LinearScale, Title, Tooltip, Legend, BarElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, Title, Tooltip, Legend, BarElement);

const options = {
  indexAxis: 'x',
  responsive: true,
  plugins: {
    legend: {
      position: 'bottom',
    },
    title: {
      display: true,
      text: 'Total Marks Obtained',
      font: {
        size: 18,
        weight: 'bold',
      },
    },
  },
  scales: {
    x: {
      grid: {
        display: false,
      },
    },
    y: {
      grid: {
        display: false,
        color: 'rgba(0, 0, 0, 0.1)',
      },
      ticks: {
        beginAtZero: true,
        stepSize: 5,
      },
    },
  },
};

const Total = () => {
  const [data1, setData1] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const url = 'http://localhost:3001/getSingleData';
      let response = await axios.get(url);
      let data = response.data;
      console.log("testshare", data[0]);
      setData1(data);
    };

    fetchData();
  }, []);

  const getChartData = () => {
    if (!data1 || data1.length === 0) {
      return {}; // Return empty object if data1 is undefined or empty
    }

    const labels = data1.map((item) => item.Date);
    const marks = data1.map((item) => item.Total_Marks_obt);

    return {
      labels: labels,
      datasets: [
        {
          label: 'Marks obtained',
          data: marks,
          backgroundColor: ['rgba(238, 125, 49, 0.8)'], // Orange with transparency
          borderColor: ['rgba(238, 125, 49, 1)'], // Orange
          borderWidth: 2,
          borderRadius: 10,
          hoverBackgroundColor: ['rgba(238, 125, 49, 1)'], // Orange (hover)
        },
      ],
    };
  };

  return (
    <div className='graph' style={{ width: '100%', maxWidth: '500px', height: '400px', margin: '60px auto' }}>
      <Bar data={getChartData()} options={options} />
    </div>
  );
};

export default Total;
