import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Apti from '../scenes/analytics/Apti';

const Aptiscorecard = () => {
  const [data1, setData1] = useState([]);
  const [data, setData] = useState([]);
  const [dates, setDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState('Date: 2021-06-01');
  useEffect(() => {
    const fetchData = async () => {
      try {
        const url = 'http://localhost:3001/getSingleData';
        const response = await axios.get(url);
        const data = response.data;
        console.log("testshare", data);
        setData1(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);
  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);

  };
  return (
    <>
    <select style={{ marginTop: '3rem' }} id="dateSelect" value={selectedDate} onChange={handleDateChange}>
      <option value="">Select a date</option>
        {dates.map((date, index) => (
          <option key={index} value={date}>
            {date}
          </option>
        ))}
      </select>
    <div className="d-flex">

    {data && data.map((data) => (
          <div className="rounded bg-white p-3" style={{ flex: '1', marginRight: '1rem' }}>
            <div className="text">
              <p>
                Attempted On <span>{data.Date}</span>
              </p>
              <p>
                Time Taken <span>45m</span>
              </p>
              <p>
                Bank <span>1/5</span>
              </p>
              <p>
                Correct <span>30%</span>
              </p>
              <p>
                Marks Obtained <span>68.00/80</span>
              </p>
              <p>
                Shipped <span></span>
              </p>
              <p>
                Time Taken <span>46m</span>
              </p>
              <p>
                Correct Questions <span>36</span>
              </p>
              <p>
                Incorrect Questions <span></span>
              </p>
              <p>
                Skipped Question <span></span>
              </p>
            </div>
          </div>
        ))}
      <div className="Agraph" style={{ flex: '2', marginLeft: '1rem' }}>
        {<Apti/>}
      </div>
    </div>
    </>
  );
};

export default Aptiscorecard
