import { Box, Button, IconButton, Typography, useTheme } from "@mui/material";
import "./index.css";
import React, { useEffect, useState } from "react";
import axios from "axios";
import MyCarousel from "../global/Slider2";
import CloudDownloadIcon from "@mui/icons-material/CloudDownload";
import NavigateBeforeIcon from "@mui/icons-material/NavigateBefore";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
// import SliderData from "../global/SliderData";

const Dashboard = () => {
  const [record, setRecord] = useState([]);
  const [marks, setMarks] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [startIndex, setStartIndex] = useState(0);
  const [notificationsPerPage] = useState(5);

  //   const fetchData = (async () => {
  const [data1, setData1] = useState();
  useEffect(() => {
    const fetchData = async () => {
      const url = "http://192.168.1.33:3001/getSingleData";

      let data = await axios.get(url);

      console.log("testshare", data.data);

      if (data) {
        setData1(data.data);
      }
    };
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://192.168.1.55:3000/data"); // Update this URL with the correct server URL
      setNotifications(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // ...

  const fetchMaxScore = (category) => {
    if (marks.length > 0) {
      const categoryScores = marks.map((entry) => entry.scores[category].score);
      const maxScore = Math.max(...categoryScores);
      return maxScore;
    }
    return 0;
  };

  const maxAptitudeScore = fetchMaxScore("aptitude");
  const maxCommunicationScore = fetchMaxScore("verbal");

  const maxTechnicalScore = fetchMaxScore("technical");
  // Display only the first 10 records
  const limitedRecords = record.slice(0, 10);

  const showNextNotifications = () => {
    setStartIndex(startIndex + notificationsPerPage);
  };

  const showPreviousNotifications = () => {
    setStartIndex(startIndex - notificationsPerPage);
  };

  const downloadPDF = (filename) => {
    const link = document.createElement("a");
    link.href = `http://localhost:3000/uploads/${filename}`;
    link.target = "_blank";
    link.download = filename;
    link.click();
  };

  const currentNotifications = notifications.slice(
    startIndex,
    startIndex + notificationsPerPage
  );

  return (
    <div className="content" style={{ backgroundColor: "white" }}>
      <div style={{ backgroundColor: "white" }}>
        <div className="col main pt-5 mt-3 container">
          <p className="lead d-none d-sm-block">Welcome tp your Dashboard</p>

          {data1 &&
            data1.map((data) => {
              return (
                <>
                  <div className="row mb-3">
                    <div className="col-xl-4 col-sm-6 py-2">
                      <div className="card text-white h-100 ">
                        <div
                          className="card-body bg-success rounded"
                          style={{
                            background:
                              "linear-gradient(to right, #1cbf4b, #0c8f2c)",
                          }}
                        >
                          <div className="rotate">
                            <i className="fa fa-code fa-4x"></i>
                          </div>

                          <h6 className="text-uppercase">Total classes</h6>
                          <h1 className="display-4">{data?.TotalAttend}</h1>
                        </div>
                      </div>
                    </div>

                    <div className="col-xl-4 col-sm-6 py-2">
                      <div className="card text-white h-100">
                        <div
                          className="card-body bg-info rounded"
                          style={{
                            background:
                              "linear-gradient( to right, #FFA500, #FF6347)",
                          }}
                        >
                          <div className="rotate">
                            <i
                              className="fa fa-cubes fa-4x"
                              aria-hidden="true"
                            ></i>
                          </div>
                          <h6 className="text-uppercase">classes attended</h6>
                          <h1 className="display-4">{data?.ClassesAttend}</h1>
                        </div>
                      </div>
                    </div>

                    <div className="col-xl-4 col-sm-6 py-2">
                      <div className="card text-white ">
                        <div
                          className="card-body bg-info rounded"
                          style={{
                            background:
                              "linear-gradient(to right, #007bff, #090979)",
                          }}
                        >
                          <div className="rotate">
                            <i
                              className="fa fa-info fa-4x"
                              aria-hidden="true"
                            ></i>
                          </div>
                          <h6 className="text-uppercase">Test Shares</h6>
                          <h1 className="display-4">{data?.TestShare}</h1>
                        </div>
                      </div>
                    </div>
                  </div>
                  <hr />
                </>
              );
            })}

          {/* --------------- */}

          <div className="row">
            <div className="col-lg-7 col-md-6 col-sm-5 col-xs-12">
              <div className="card">
                <div className="card-header">
                  <h2>
                    <i className="fa fa-hand-pointer-o" aria-hidden="true"></i>{" "}
                    Test Information
                  </h2>
                </div>
                <div className="card-body">
                  <table className="table table-striped table-hover">
                    <MyCarousel />
                  </table>
                </div>
              </div>
            </div>

            <div className="col-lg-5 col-md-6 col-sm-12">
              <div className="card">
                <div className="card-header">
                  <h2>
                    <i className="fa fa-file-alt"></i> Notifications
                  </h2>
                </div>
                <div className="card-body">
                  <div className="list-group">
                    {currentNotifications.map((notification, index) => (
                      <div
                        className="list-group-item list-group-item-action flex-column align-items-start"
                        key={index}
                      >
                        <div className="d-flex w-100 justify-content-between">
                          <h5 className="mb-1">{notification.originalname}</h5>
                          <small>{notification.date}</small>
                        </div>
                        <p className="mb-1">{notification.description}</p>
                        <small>Company: {notification.companyName}</small>
                        <IconButton
                          className="mt-2"
                          onClick={() => downloadPDF(notification.filename)}
                          style={{
                            color: "blue",
                            marginLeft: "auto",
                            float: "right",
                          }}
                        >
                          <CloudDownloadIcon />
                        </IconButton>
                      </div>
                    ))}
                  </div>
                  <div className="pagination-buttons">
                    {startIndex > 0 && (
                      <IconButton
                        onClick={showPreviousNotifications}
                        style={{
                          color: "blue",
                          marginLeft: "auto",
                          float: "left",
                        }}
                      >
                        <NavigateBeforeIcon />
                      </IconButton>
                    )}
                    {startIndex + notificationsPerPage <
                      notifications.length && (
                      <IconButton
                        onClick={showNextNotifications}
                        style={{
                          color: "blue",
                          marginLeft: "auto",
                          float: "right",
                        }}
                      >
                        <NavigateNextIcon />
                      </IconButton>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
