import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Total from '../scenes/analytics/Total';
import OptionMenu from './OptionMenu';

const Totalscoreacard = () => {
  const [data, setData] = useState([]);
  const [dates, setDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');

  useEffect(() => {
    const fetchDates = async () => {
      try {
        const url = 'http://localhost:3001/getSingleData'; // Replace with your endpoint to fetch 
        const response = await axios.get(url);
        const datesData = response.data.map(date => {
          const dateObject = new Date(date.Date);
          const formattedDate = dateObject.toLocaleDateString('en-US', { year: 'numeric', month: '2-digit', day: '2-digit' });
          return formattedDate;
        });
        console.log("testshare::::", datesData);
        setDates(datesData);
      } catch (error) {
        console.error('Error fetching dates:', error);
      }
    };

    fetchDates();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const url = 'http://localhost:3001/getDateData';
        const response = await axios.get(url, {
          params: {
            date: selectedDate,
          },
        });
        const data = response.data;
        // console.log("testshare::::", data);
        setData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [selectedDate]);

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
    const parts = selectedDate.split('/');
    const formattedDate = `${parts[2]}-${parts[1]}-${parts[0]}T18:30:00.000+00:00`;
    setSelected
  };

  return (
    <>
      <select style={{ marginTop: '3rem' }} id="dateSelect" value={selectedDate} onChange={handleDateChange}>
        {dates.map((date, index) => (
          <option key={index} value={date}>
            {date}
          </option>
        ))}
      </select>
      <div className="d-flex">
        {data.map((data) => (
          <div className="rounded bg-white p-3" style={{ flex: '1', marginRight: '1rem' }}>
            <div className="text">
              <p>
                Attempted On <span>{data.Date}</span>
              </p>
              <p>
                Time Taken <span>45m</span>
              </p>
              <p>
                Bank <span>1/5</span>
              </p>
              <p>
                Correct <span>30%</span>
              </p>
              <p>
                Marks Obtained <span>68.00/80</span>
              </p>
              <p>
                Shipped <span></span>
              </p>
              <p>
                Time Taken <span>46m</span>
              </p>
              <p>
                Correct Questions <span>36</span>
              </p>
              <p>
                Incorrect Questions <span></span>
              </p>
              <p>
                Skipped Question <span></span>
              </p>
            </div>
          </div>
        ))}
        <div className="Agraph" style={{ flex: '2', marginLeft: '1rem' }}>
          <Total />
        </div>
      </div>
    </>
  );
};

export default Totalscoreacard;
